package com.imooie.maximum;import android.content.Context;import android.content.Intent;import android.os.Bundle;import android.support.annotation.NonNull;import android.support.design.widget.BottomNavigationView;import android.support.v7.app.AppCompatActivity;import android.view.MenuItem;import android.view.View;import android.widget.Button;import android.widget.EditText;import android.widget.Toast;public class ContactUsActivity extends AppCompatActivity {    private final Context mContext = this;    @Override    protected void onCreate(Bundle savedInstanceState) {        super.onCreate(savedInstanceState);        setContentView(R.layout.activity_contact_us);        BottomNavigationView bottomNavigationView = (BottomNavigationView) findViewById(R.id.bottom_navigation);        BottomNavigationViewHelper.disableShiftMode(bottomNavigationView);        bottomNavigationView.setOnNavigationItemSelectedListener(new BottomNavigationView.OnNavigationItemSelectedListener() {            @Override            public boolean onNavigationItemSelected(@NonNull MenuItem item) {                switch (item.getItemId()) {                    case R.id.action_item1:                        finish();                        startActivity(new Intent(mContext, Common_QuestionsActivity.class));                        break;                    case R.id.action_item3:                        finish();                        startActivity(new Intent(mContext, MainActivity.class));                        break;                    case R.id.action_item4:                        finish();                        startActivity(new Intent(mContext, VideosActivity.class));                        break;                    case R.id.action_item5:                        finish();                        startActivity(new Intent(mContext, StuffActivity.class));                        break;                }                return false;            }        });        Button mBtnFeedback = (Button) findViewById(R.id.submit);        final EditText ET_to = (EditText) findViewById(R.id.ET_to);        final EditText ET_subject = (EditText) findViewById(R.id.ET_subject);        final EditText ET_message = (EditText) findViewById(R.id.ET_message);        mBtnFeedback.setOnClickListener(new View.OnClickListener() {            @Override            public void onClick(View v) {                String to = ET_to.getText().toString();                String message = ET_message.getText().toString();                String subject = ET_subject.getText().toString();                Intent mEmail = new Intent(Intent.ACTION_SEND);                mEmail.putExtra(Intent.EXTRA_EMAIL, new String[]{to});                mEmail.putExtra(Intent.EXTRA_SUBJECT, subject);                mEmail.putExtra(Intent.EXTRA_TEXT, message);                mEmail.setType("message/rfc822");                try {                    startActivity(Intent.createChooser(mEmail, "Send mail ..."));                } catch (android.content.ActivityNotFoundException ex) {                    Toast.makeText(mContext, "There are no email clients installed.", Toast.LENGTH_SHORT).show();                }            }        });    }    public void onBackPressed() {        Intent a = new Intent(this, MainActivity.class);        startActivity(a);    }}